query pageQuery(
  $id: String
) {
  contentfulBlogPost(id: {eq: $id}) {
    id
    slug
    title
    publishDate
    description {
      description
    }
    body {
      body
    }
    author {
      title
      phone
      company
    }
    heroImage {
      sizes {
        src
        srcWebp
      }
      description
      file {
        url
        fileName
        contentType
      }
    }
  }
}

